// src/pages/Home.js
import React, { useEffect, useState } from "react";
import { regularHours, specialHours } from "../data";
import RegularHours from "../components/RegularHours";
import SpecialHours from "../components/SpecialHours";

const Home = () => {
  const [todaySpecialHours, setTodaySpecialHours] = useState([]);

  useEffect(() => {
    const today = new Date().toISOString().split("T")[0]; // Get today's date in YYYY-MM-DD
    const allSpecialHours = JSON.parse(localStorage.getItem("specialHours")) || specialHours.data;

    const todayHours = allSpecialHours.filter((hour) => hour.date === today);
    setTodaySpecialHours(todayHours);
  }, []);

  return (
    <div>
      <RegularHours />
      {todaySpecialHours.length > 0 && <SpecialHours hours={todaySpecialHours} />}
    </div>
  );
};

export default Home;
