// src/pages/ManageSpecialHours.js
import React, { useState } from "react";

const ManageSpecialHours = () => {
  const [date, setDate] = useState("");
  const [openTime, setOpenTime] = useState("");
  const [closeTime, setCloseTime] = useState("");
  const [reason, setReason] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Retrieve current special hours from local storage
    const storedHours = JSON.parse(localStorage.getItem("specialHours")) || [];

    // Add the new special hour
    const newHour = { id: Date.now(), date, openTime, closeTime, reason };
    const updatedHours = [...storedHours, newHour];

    // Save back to local storage
    localStorage.setItem("specialHours", JSON.stringify(updatedHours));

    // Reset form fields
    setDate("");
    setOpenTime("");
    setCloseTime("");
    setReason("");
    alert("Special hours added successfully!");
  };

  return (
    <div>
      <h2>Manage Special Hours</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date:</label>
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <div>
          <label>Open Time:</label>
          <input type="time" value={openTime} onChange={(e) => setOpenTime(e.target.value)} required />
        </div>
        <div>
          <label>Close Time:</label>
          <input type="time" value={closeTime} onChange={(e) => setCloseTime(e.target.value)} required />
        </div>
        <div>
          <label>Reason:</label>
          <input type="text" value={reason} onChange={(e) => setReason(e.target.value)} required />
        </div>
        <button type="submit">Add Special Hours</button>
      </form>
    </div>
  );
};

export default ManageSpecialHours;
