// src/components/CurrentWeek.js
export const getCurrentWeekSpecialHours = (specialHours) => {
    const today = new Date();
    const currentWeekStart = new Date(today.setDate(today.getDate() - today.getDay())); // Start of week
    const currentWeekEnd = new Date(today.setDate(today.getDate() + 6)); // End of week

    return specialHours.filter((hour) => {
      const date = new Date(hour.date);
      return date >= currentWeekStart && date <= currentWeekEnd;
    });
  };
