// src/components/SpecialHours.js
import React from "react";
import { getTodayDate } from "../utils";

const SpecialHours = ({ hours }) => {
  const today = getTodayDate();
  const todaySpecialHours = hours.find((hour) => hour.date === today);

  return (
    <div>
      {todaySpecialHours ? (
        <div className="highlight">
          <h3>Special Hours</h3>
          <p>
            {todaySpecialHours.openTime} â€“ {todaySpecialHours.closeTime} ({todaySpecialHours.reason})
          </p>
        </div>
      ) : (
        <p>No special hours for today.</p>
      )}
    </div>
  );
};

export default SpecialHours;
