// src/components/SpecialHours.js
import React from "react";
import { isToday, formatTime } from "../utils";

const SpecialHours = ({ hours }) => {
  const todayHours = hours.filter((hour) => isToday(hour.date));

  return (
    <div>
      {todayHours.length > 0 && (
        <div>
          <h3>Special Hours</h3>
          {todayHours.map((hour) => (
            <p key={hour.id}>
              {formatTime(hour.openTime)} â€“ {formatTime(hour.closeTime)} ({hour.reason})
            </p>
          ))}
        </div>
      )}
    </div>
  );
};

export default SpecialHours;
