// src/pages/Home.js
import React, { useEffect, useState } from "react";
import { regularHours, specialHours } from "../data";

const Home = () => {
  const [todaySpecialHours, setTodaySpecialHours] = useState(null);

  useEffect(() => {
    // Get today's date in YYYY-MM-DD format
    const today = new Date().toISOString().split("T")[0];

    // Combine local storage special hours with hardcoded JSON special hours
    const storedHours = JSON.parse(localStorage.getItem("specialHours")) || [];
    const allSpecialHours = [...specialHours.data, ...storedHours];

    // Check if there's any special hour for today
    const specialForToday = allSpecialHours.find((hour) => hour.date === today);
    setTodaySpecialHours(specialForToday);
  }, []);

  return (
    <div>
      <h2>Customer Service Hours</h2>
      <ul>
        <li>Monday – Thursday: 8am ET – 7:45pm ET</li>
        <li>Friday: 9am ET – 7:45pm ET</li>
        <li>Saturday – Sunday: 9am ET – 5:45pm ET</li>
      </ul>

      {todaySpecialHours && (
        <div style={{ color: "red", fontWeight: "bold" }}>
          <h3>Special Hours: {todaySpecialHours.openTime} to {todaySpecialHours.closeTime} ({todaySpecialHours.reason})</h3>
        </div>
      )}
    </div>
  );
};

export default Home;
